let popup=document.getElementById("popup"),popupToggle=document.getElementById("btnPopup"),popupClose=document.querySelector(".close");popupToggle.onclick=function(){popup.classList.add("popup--opened"),document.body.classList.add("lock")},popupClose.onclick=function(){popup.classList.remove("popup--opened"),document.body.classList.remove("lock")},window.onclick=function(o){o.target==popup&&(popup.classList.remove("popup--opened"),document.body.classList.remove("lock"))};